import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import CardGroup from 'react-bootstrap/CardGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { loadCheckoutWebComponents } from '@checkout.com/checkout-web-components';


const Flow = () => {
    const [loading, setLoading] = useState(false);
    const [sessionData, setSessionData] = useState(null); // Store session data
    const [flowInstance, setFlowInstance] = useState(null);
    const publicKey = '{pk_sbox_z6zxchef4pyoy3bziidwee4clm4}';

const SessionRequest = async () => {
    setLoading(true);
    // Set loading to true when the request starts
    try {
        const response = await axios.post("http://127.0.0.1:5000/api/create-payment-session", {
            amount: 5000,  // Amount in cents ($50.00)
            email: "test@example.com"
        });
        console.log("Payment Responses:", response.data);
        console.log("Session ID:", response.data.session_id);
        setSessionData(response.data); // Store session data in state

        const paymentSession = response.data.session_id;
        //Init flow 
        // Initialise a new Checkout Web Components instance
        const checkout = await loadCheckoutWebComponents({
            paymentSession,
            publicKey,
            environment: 'sandbox'
        });

    } catch (error) {
        console.error("Payment Error:", error.response ? error.response.data : error.message);
    }finally {
        setLoading(false);
    }
};


const startPayment = () => {
    if (flowInstance) {
        flowInstance.mount('#flow-container');
    } else {
        console.error("Flow instance not initialized.");
    }
};

return(
    <div>
        <br></br>
    <div>
    <CardGroup>
        <Card>
            <Card.Body>
            <Card.Title>Flow</Card.Title>
            <Card.Text>
            <div>
                <button onClick={SessionRequest} disabled={loading}>
                    {loading ? "Processing..." : "Request Session"}
                </button>

                <br></br>
                <div>
                {sessionData && (
                 <p>Payment Session ID: {sessionData.session_id}</p>
                )}
                </div>
            </div>
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
        <Card>
            <Card.Body>
            <Card.Title>Flow - Button</Card.Title>
            <Card.Text>
            {flowInstance && (
                <button onClick={startPayment}>
                    Start Payment
                </button>
            )}
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
        <Card>
            <Card.Body>
            <Card.Title>Frames</Card.Title>
            <Card.Text>
                This is a wider card with supporting text below as a natural lead-in
                to additional content. This card has even longer content than the
                first to show that equal height action.
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
    </CardGroup> 
    </div>
</div>
    );
};


export default Flow;
