import axios from "axios";
import Button from 'react-bootstrap/Button';
import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import CardGroup from 'react-bootstrap/CardGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


const RequestPayment = async () => {
    try {
        const response = await axios.post("http://127.0.0.1:5000/api/requestPayment", {
            card_number: "4242424242424242",
            expiry_month: "6",
            expiry_year: "2025",
            cvv: "100",
            amount: 5000,  // Amount in cents ($50.00)
            email: "test@example.com"
        });

        console.log("Payment Response:", response.data);
    } catch (error) {
        console.error("Payment Error:", error.response ? error.response.data : error.message);
    }
return(
    <div>
    <h2>Checkout</h2>
    {sessionId ? (
        <iframe
            src={`https://pay.checkout.com/flow/${sessionId}`}
            width="400"
            height="600"
            style={{ border: "none" }}
        ></iframe>
    ) : (
        <p>Loading payment session...</p>
    )}
    <div>
    <CardGroup>
        <Card>
            <Card.Body>
            <Card.Title>Flow</Card.Title>
            <Card.Text>
                This is a wider card with supporting text below as a natural lead-in
                to additional content. This content is a little bit longer.
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
        <Card>
            <Card.Body>
            <Card.Title>Flow - Tokenisation Only</Card.Title>
            <Card.Text>
                This card has supporting text below as a natural lead-in to
                additional content.
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
        <Card>
            <Card.Body>
            <Card.Title>Frames</Card.Title>
            <Card.Text>
                This is a wider card with supporting text below as a natural lead-in
                to additional content. This card has even longer content than the
                first to show that equal height action.
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
    </CardGroup> 
    </div>
</div>
    );
};


export default RequestPayment;
