import axios from "axios";
import Button from 'react-bootstrap/Button';
import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import CardGroup from 'react-bootstrap/CardGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

const PaymentLink = () => {
    const [loading, setLoading] = useState(false);


const RequestPaymentLink = async () => {
    try {
        const response = axios.post("http://127.0.0.1:5000/api/paymentLink", {
        });

        console.log("Payment Response:", response.data);
    } catch (error) {
        console.error("Payment Error:", error.response ? error.response.data : error.message);
    }finally {
        setLoading(false);
    }
};
return(
    <div>
        <br></br>
    <div>
    <CardGroup>
        <Card>
            <Card.Body>
            <Card.Title>Payment Link Demo</Card.Title>
            <Card.Text>
            <div>
                <button onClick={RequestPaymentLink} disabled={loading}>
                    {loading ? "Processing..." : "Requesting Payment Link"}
                </button>
            </div>
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
    </CardGroup> 
    </div>
</div>
    );
};


export default PaymentLink;
