import React, { useState, useEffect, useRef } from 'react';
import { Card } from 'react-bootstrap';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import CardGroup from 'react-bootstrap/CardGroup';
import { loadCheckoutWebComponents } from '@checkout.com/checkout-web-components';

const ApplePay = () => {
    const containerRef = useRef(null);

    useEffect(() => {
        // Dynamically create the Apple Pay button when the component mounts
        const applePayButton = document.createElement('apple-pay-button');
        applePayButton.setAttribute('buttonstyle', 'black');
        applePayButton.setAttribute('type', 'plain');
        applePayButton.setAttribute('locale', 'en-US');

        // Append the button to the container ref
        if (containerRef.current) {
            containerRef.current.appendChild(applePayButton);
        }
    }, []);


return(
    <div>
        <br></br>
        <h1 className="text-center">Apple Pay</h1>
    <div>
    <CardGroup>
        <Card>
        <Card.Body>
            <Card.Title className="text-center">To-Do</Card.Title>
            <Card.Text>
                - Request a session for Apple Pay<br></br>
                - Load Apple Pay button<br></br>
                - Handle Apple Pay button click<br></br>
                - Add .pem and .key files to server<br></br>
                - Host Website and verify domain in dev account<br></br>
            </Card.Text>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
        <Card>
            <Card.Body>
            <Card.Title className="text-center">Load Apple Pay Buttons</Card.Title>
            <style>
                {`
                apple-pay-button {
                    --apple-pay-button-width: 150px;
                    --apple-pay-button-height: 30px;
                    --apple-pay-button-border-radius: 3px;
                    --apple-pay-button-padding: 0px 0px;
                    --apple-pay-button-box-sizing: border-box;
                }
                `}
            </style>
            <div ref={containerRef}></div>
            </Card.Body>
            <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
        </Card>
        <Card>

        </Card>
    </CardGroup> 
    </div>
</div>
    );
};


export default ApplePay;
