import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import CardGroup from 'react-bootstrap/CardGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

const Flow = () => {
    const [sessionId, setSessionId] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:5000/api/create-payment-session", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ amount: 5000, email: "test@example.com" })
        })
        .then(res => res.json())
        .then(data => setSessionId(data.session_id))
        .catch(err => console.error(err));
    }, []);

    useEffect(() => {
        if (sessionId) {
            window.Frames.init({ publicKey: "pk_sbox_z6zxchef4pyoy3bziidwee4clm4" });
            window.Frames.addEventHandler("onComplete", (event) => {
                console.log("Payment successful:", event);
            });
        }
    }, [sessionId]);

    return (
        <div>
            <h2>Checkout</h2>
            {sessionId ? (
                <iframe
                    src={`https://pay.checkout.com/flow/${sessionId}`}
                    width="400"
                    height="600"
                    style={{ border: "none" }}
                ></iframe>
            ) : (
                <p>Loading payment session...</p>
            )}
            <div>

            </div>
        </div>
    );
};

export default Flow;
